mapper.update:
UPDATE pet_info 
SET pet_name = #{pet_name}, pet_gender = #{pet_gender}, pet_code = #{pet_code}, pet_kind = #{pet_kind}, pet_birthdate = #{pet_birthdate}, pet_weight = #{pet_weight}, pet_size = #{pet_size}, neuter = #{neuter}, animalhospital_name = #{animalhospital_name}, animalhospital_addr = #{animalhospital_addr}, pet_note = #{pet_note}       
WHERE pet_id = #{pet_id}
